class HashTable {

    constructor(size) {
        this.size = size;
        this.array = Array(size);
    }

    hashFunction(key){
        let hashConst = 11;
        for (let i=0; i<key.length; i++){
            hashConst = (19 * hashConst * key.charCodeAt(i)) % this.size;
        }
    }

    insert(key, value){
        let index = this.hashFunction(key);
        if (this.array[index]){
            this.array[index].push([key, value]);
        } else {
            this.array[index] = [[key, value]];
        }
    }

    search(key){
        let index = this.hashFunction(key);
        if(!this.array[index])
            return null;
        return this.array[index].find(x => x[0] === key)[1];
    }
}

let hash = new HashTable(9);
hash.insert('Lewis', {name: 'Lewis', id: 1033});
hash.insert('Dani', {name: 'Dani', id: 1099});
console.log(hash.search('Lewis'));
console.log(hash.search('Dani'));
export default HashTable;
