class HashNode{
    constructor(key, value) {
        this.key = key;
        this.value = value;
    }
}

class HashTable {

    constructor(size) {
        this.size = size;
        this.array = Array(size);
    }

    hashFunction(key){
        let hashConst = 11;
        for (let i=0; i<key.length; i++){
            hashConst = (hashConst * key.charCodeAt(i)) % this.size;
        }
    }

    insert(key, value){
        const index = this.hashFunction(key);
        this.array[index] = value;
    }

    search(key){
        const index = this.hashFunction(key);
        return this.array[index];
    }
}

let hash = new HashTable(100);
hash.insert(15, {name: 'Lewis', id: 1033});
console.log(hash.search(15));

export default HashTable;
