//import users1 from "../../data/users";
class HashTable {

    constructor(size) {
        this.size = size;
        this.array = Array(size);
        this.items = 0;
    }

    hashFunction(key){
        let hashConst = 11;
        for (let i=0; i<key.length; i++){
            hashConst = (19 * hashConst * key.charCodeAt(i)) % this.size;
        }
    }

    insert(key, value){
        if(!this.search(key)){
            let index = this.hashFunction(key);
            if (this.array[index]){
                this.array[index].push([key, value]);
            } else {
                this.array[index] = [[key, value]];
            }
            this.items++;
        }
    }

    search(key){
        let index = this.hashFunction(key);
        if(!this.array[index])
            return null;
        return this.array[index].find(x => x[0] === key)[1];
    }
}

let hash = new HashTable(37);
hash.insert('leon', {name: 'leon', phone: 123});
hash.insert('dua', {name: 'dua', phone: 333});
console.log(hash.search('dua'));

