        this.size = size;
        this.array = Array(size);
        this.items = 0;
        this.loadFactor = this.items/this.size;
    }

    hashFunction(key){
        let hashConst = 11;
        for (let i=0; i<key.length; i++){
            hashConst = (19 * hashConst * key.charCodeAt(i)) % this.size;
        }
    }

    insert(key, value){
        if(this,this.loadFactor >= 0.8){
            let copy = this.array;
            this.array = Array(this.size*2);

        }
        if(!this.search(key)){
            let index = this.hashFunction(key);
            if (this.array[index]){
                this.array[index].push([key, value]);
            } else {
                this.array[index] = [[key, value]];
            }
            this.items++;
        }
    }

    search(key){
        let index = this.hashFunction(key);
        if(!this.array[index])
            return null;
        return this.array[index].find(x => x[0] === key)[1];
