import React, {Component} from "react";
import product_linked_list from "./components/ProductLinkedListDriver";

const ProductContext = React.createContext();

class ProductProviderLinkedList extends Component{
    state = {
        products: product_linked_list.array
    }

    handleDetail = () =>{
        console.log("This is your product Details");
    }

    addToCar = () => {
        console.log("Added to yor shopping car");
    }

    render() {
        return(
            <ProductContext.Provider value={{
                ...this.state,
                addToCar: this.addToCar(),
                handleDetail: this.handleDetail()
            }}>
                {this.props.children}
            </ProductContext.Provider>
        );
    }
}

const ProductConsumerLinkedList = ProductContext.Consumer;

export {ProductProviderLinkedList, ProductConsumerLinkedList};
